(10, 1, '2017-09-12 08:45:57', '2017-09-12 08:45:57', '{\n    \"custom_css[stanpak]\": {\n        \"value\": \"/*\\nB\\u1ea1n c\\u00f3 th\\u1ec3 th\\u00eam CSS \\u1edf \\u0111\\u00e2y.\\n\\nNh\\u1ea5p chu\\u1ed9t v\\u00e0o bi\\u1ec3u t\\u01b0\\u1ee3ng tr\\u1ee3 gi\\u00fap ph\\u00eda tr\\u00ean \\u0111\\u1ec3 t\\u00ecm hi\\u1ec3u th\\u00eam.\\n*/\\nhtml,\\nbody {\\n  width: 100%;\\n  height: 100%; }\\n\\nbody {\\n  font-family: \'Muli\', \'Helvetica\', \'Arial\', \'sans-serif\'; }\\n\\na {\\n  color: #fdcc52;\\n  -webkit-transition: all .35s;\\n  -moz-transition: all .35s;\\n  transition: all .35s; }\\n  a:hover, a:focus {\\n    color: #fcbd20; }\\n\\nhr {\\n  max-width: 100px;\\n  margin: 25px auto 0;\\n  border-width: 1px;\\n  border-color: rgba(34, 34, 34, 0.1); }\\n\\nhr.light {\\n  border-color: white; }\\n\\nh1,\\nh2,\\nh3,\\nh4,\\nh5,\\nh6 {\\n  font-family: \'roboto\', \'Helvetica\', \'Arial\', \'sans-serif\';\\n  font-weight: 200;\\n  letter-spacing: 1px; }\\n\\np {\\n  font-size: 18px;\\n  line-height: 1.5;\\n  margin-bottom: 20px; }\\n\\nsection {\\n  padding: 100px 0; }\\n  section h2 {\\n    font-size: 50px; }\\n\\n#mainNav {\\n  border-color: rgba(34, 34, 34, 0.05);\\n  background-color: white;\\n  -webkit-transition: all .35s;\\n  -moz-transition: all .35s;\\n  transition: all .35s;\\n  font-family: \'Catamaran\', \'Helvetica\', \'Arial\', \'sans-serif\';\\n  font-weight: 200;\\n  letter-spacing: 1px; }\\n  #mainNav .navbar-brand {\\n    color: #fdcc52;\\n    font-family: \'Catamaran\', \'Helvetica\', \'Arial\', \'sans-serif\';\\n    font-weight: 200;\\n    letter-spacing: 1px; }\\n  #mainNav .navbar-brand img{\\n    width: 120px;\\n  }\\n  @media(max-width: 768px){\\n    #mainNav .navbar-brand img{\\n      width: 63px;\\n    }\\n  }\\n    #mainNav .navbar-brand:hover, #mainNav .navbar-brand:focus {\\n      color: #fcbd20; }\\n  #mainNav .navbar-toggler {\\n    font-size: 12px;\\n    padding: 8px 10px;\\n    color: #222222; }\\n  #mainNav .navbar-nav > li > a {\\n    font-size: 11px;\\n    font-family: \'Lato\', \'Helvetica\', \'Arial\', \'sans-serif\';\\n    letter-spacing: 2px;\\n    text-transform: uppercase; }\\n    #mainNav .navbar-nav > li > a.active {\\n      color: #fdcc52 !important;\\n      background-color: transparent; }\\n      #mainNav .navbar-nav > li > a.active:hover {\\n        background-color: transparent; }\\n  #mainNav .navbar-nav > li > a,\\n  #mainNav .navbar-nav > li > a:focus {\\n    color: #222222; }\\n    #mainNav .navbar-nav > li > a:hover,\\n    #mainNav .navbar-nav > li > a:focus:hover {\\n      color: #fdcc52; }\\n  @media (min-width: 992px) {\\n    #mainNav {\\n      border-color: transparent;\\n      background-color: transparent; }\\n      #mainNav .navbar-brand {\\n        color: fade(white, 70%); }\\n        #mainNav .navbar-brand:hover, #mainNav .navbar-brand:focus {\\n          color: white; }\\n      #mainNav .navbar-nav > li > a,\\n      #mainNav .navbar-nav > li > a:focus {\\n        color: rgba(0, 0, 0, 0.7); }`\\n        #mainNav .navbar-nav > li > a:hover,\\n        #mainNav .navbar-nav > li > a:focus:hover {\\n          color: white; }\\n      #mainNav.navbar-shrink {\\n        border-color: rgba(34, 34, 34, 0.1);\\n        background-color: white; }\\n        #mainNav.navbar-shrink .navbar-brand {\\n          color: #222222; }\\n          #mainNav.navbar-shrink .navbar-brand:hover, #mainNav.navbar-shrink .navbar-brand:focus {\\n            color: #fdcc52; }\\n        #mainNav.navbar-shrink .navbar-nav > li > a,\\n        #mainNav.navbar-shrink .navbar-nav > li > a:focus {\\n          color: #222222; }\\n          #mainNav.navbar-shrink .navbar-nav > li > a:hover,\\n          #mainNav.navbar-shrink .navbar-nav > li > a:focus:hover {\\n            color: #fdcc52; } \\n    #navbarResponsive{\\n      /*display: none!important;*/\\n      position: absolute;\\n      right: 0px;\\n      top: 0;\\n      background-color: rgba(0,0,0,.35);\\n      height: 100vh;\\n      -webkit-transition: width .5s ease-in-out;\\n      -moz-transition: width .5s ease-in-out;\\n      -o-transition: width .5s ease-in-out;\\n      transition: width .5s ease-in-out;  \\n      width: 0px;\\n      visibility: hidden;\\n    }\\n    .navbar-expand-lg .navbar-nav{\\n          flex-direction: column;\\n          text-align: right;\\n    }\\n    #mainNav .navbar-nav > li > a{\\n        font-size: 30px;\\n        font-family: \'roboto\';\\n        font-weight: 900\\n\\n    }\\n  }\\n  #navbarResponsive.open{\\n    width: 20%;\\n    visibility: visible;\\n  }\\n.navbar-toggle-desktop{\\n   width: 30px;\\n  height: 30px;\\n  position: relative;\\n  /*margin: 50px auto;*/\\n  -webkit-transform: rotate(0deg);\\n  -moz-transform: rotate(0deg);\\n  -o-transform: rotate(0deg);\\n  transform: rotate(0deg);\\n  -webkit-transition: .5s ease-in-out;\\n  -moz-transition: .5s ease-in-out;\\n  -o-transition: .5s ease-in-out;\\n  transition: .5s ease-in-out;\\n  cursor: pointer;\\n}\\n.navbar-toggle-desktop span{\\n  display: block;\\n  position: absolute;\\n  height: 5px;\\n  width: 100%;\\n  background: #1C6BC8;\\n  border-radius: 9px;\\n  opacity: 1;\\n  left: 0;\\n  -webkit-transform: rotate(0deg);\\n  -moz-transform: rotate(0deg);\\n  -o-transform: rotate(0deg);\\n  transform: rotate(0deg);\\n  -webkit-transition: .25s ease-in-out;\\n  -moz-transition: .25s ease-in-out;\\n  -o-transition: .25s ease-in-out;\\n  transition: .25s ease-in-out;\\n}\\n.navbar-toggle-desktop span:nth-child(1) {\\n  top: 0px;\\n}\\n\\n.navbar-toggle-desktop span:nth-child(2) {\\n  top: 9px;\\n}\\n\\n.navbar-toggle-desktop span:nth-child(3) {\\n  top: 18px;\\n}\\n.navbar-toggle-desktop.open{\\n  position: absolute;\\n  right: 50px;\\n  top: 50px;\\n  z-index: 9999;\\n}\\n.navbar-toggle-desktop.open span:nth-child(1) {\\n  top: 9px;\\n  -webkit-transform: rotate(135deg);\\n  -moz-transform: rotate(135deg);\\n  -o-transform: rotate(135deg);\\n  transform: rotate(135deg);\\n  background: #DE3091;\\n}\\n\\n.navbar-toggle-desktop.open span:nth-child(2) {\\n  opacity: 0;\\n  left: -60px;\\n  background: #DE3091;\\n}\\n\\n.navbar-toggle-desktop.open span:nth-child(3) {\\n  top: 9px;\\n  -webkit-transform: rotate(-135deg);\\n  -moz-transform: rotate(-135deg);\\n  -o-transform: rotate(-135deg);\\n  transform: rotate(-135deg);\\n  background: #DE3091;\\n}\\n\\n.navbar-toggle-desktop:focus{\\n  outline: 0;\\n}\\n@media (max-width: 768px) {\\n    .navbar-toggle-desktop{\\n      display: none!important;\\n    }\\n\\n  }\\nheader.masthead {\\n  position: relative;\\n  width: 100%;\\n  padding-top: 150px;\\n  /*padding-bottom: 100px;*/\\n  color: white;/*\\n  background: url(\\\"../img/bg-pattern.png\\\"), #7b4397;\\n  background: url(\\\"../img/bg-pattern.png\\\"), -webkit-linear-gradient(to left, #7b4397, #dc2430);\\n  background: url(\\\"../img/bg-pattern.png\\\"), linear-gradient(to left, #7b4397, #dc2430); */\\n  /*background: url(\\\"../img/s7-batman.jpg\\\")no-repeat center right, #000;*/\\n  /*background: url(\\\"../img/bg-pattern.png\\\");\\n  background: url(\\\"../img/bg-pattern.png\\\"); */\\n}\\n  header.masthead .header-content {\\n    max-width: 500px;\\n    margin-bottom: 100px;\\n    text-align: center; }\\n    header.masthead .header-content h1 {\\n      font-size: 30px; }\\n  header.masthead .device-container {\\n    max-width: 325px;\\n    margin-right: auto;\\n    margin-left: auto; }\\n    header.masthead .device-container .screen img {\\n      border-radius: 3px; }\\n  @media (min-width: 992px) {\\n    header.masthead {\\n      height: 100vh;\\n      min-height: 550px;\\n      padding-top: 0;\\n      padding-bottom: 0; }\\n      header.masthead .header-content {\\n        margin-bottom: 0;\\n        text-align: left; }\\n        header.masthead .header-content h1 {\\n          font-size: 50px; }\\n      header.masthead .device-container {\\n        max-width: 325px; } \\n      }\\n  @media (min-width: 320px) {\\n    header.masthead {\\n      /*height: 100vh;\\n      min-height: 775px;*/\\n      padding-top: 0;\\n      padding-bottom: 0; }\\n      header.masthead .header-content {\\n        margin-bottom: 0;\\n        text-align: left; }\\n        header.masthead .header-content h1 {\\n          font-size: 50px; }\\n      header.masthead .device-container {\\n        max-width: 325px; } \\n        .carousel-caption h2{\\n          font-size: 1.5rem;\\n        }\\n        }\\n\\nsection.download {\\n  position: relative;\\n  padding: 150px 0; }\\n  section.download h2 {\\n    text-align: left;\\n    font-size: 50px;\\n    margin-top: 0; }\\n  .section-heading.text-center {\\n    margin-bottom: 100px;\\n  }\\n  section.project .img-project{\\n        display: inline-table;\\n  }\\n  section.project .img-project .item{\\n    width: 33.33%;\\n    max-height: 300px;\\n    display: inline-table;\\n    overflow: hidden;\\n    padding: 0;\\n    margin: 0;\\n    position: relative;\\n  }\\n  section.project .img-project .item .item-info{\\n    position: absolute;\\n    background-color: rgba(0,0,0,0.55);\\n    top: 0;\\n    left: 0;\\n    right: 0;\\n    bottom: 0;\\n    vertical-align: middle;\\n    padding-top: 20%;\\n    transition: all 0.2s ease-in;\\n    -moz-transform: scale(0);\\n    -webkit-transform: scale(0);\\n    transform: scale(0);\\n\\n  }\\n  section.project .img-project img{\\n    width: 100%;\\n    transition: all 0.3s ease-in-out;\\n  }\\n  section.project .img-project a:hover .item-info{\\n    -moz-transform: scale(1);\\n    -webkit-transform: scale(1);\\n    transform: scale(1);\\n  }\\n  section.project .img-project a:hover img{\\n    -moz-transform: scale(1.1);\\n    -webkit-transform: scale(1.1);\\n    transform: scale(1.1);\\n  }\\n  @media (max-width: 992px) {\\n    section.project .img-project .item{\\n      width: 20%;\\n    }\\n  }\\n  @media (max-width: 768px) {\\n    section.project .img-project .item{\\n      width: 50%;\\n    }\\n\\n  }\\n  @media (max-width: 414px) {\\n    section.project .img-project .item{\\n      width: 100%;\\n    }\\n  }\\n\\n  section.download .badges .badge-link {\\n    display: block;\\n    margin-bottom: 25px; }\\n    section.download .badges .badge-link:last-child {\\n      margin-bottom: 0; }\\n    section.download .badges .badge-link img {\\n      height: 60px; }\\n    @media (min-width: 768px) {\\n      section.download .badges .badge-link {\\n        display: inline-block;\\n        margin-bottom: 0; } }\\n  @media (min-width: 768px) {\\n    section.download h2 {\\n      font-size: 70px; } \\n}\\nsection.features{\\n  color: #fff;\\n}\\nsection.features p{\\n  color: #cee5ff!important;\\n}\\nsection.features .avatar{\\n  padding-top: 20%;\\n  flex-wrap:nowrap;\\n}\\nsection.features .avatar div{\\n      height: auto;\\n    border-radius: 5px;\\n  overflow: hidden;\\n  padding:0;\\n  margin: 0;\\n  margin-right: 10px;\\n}\\nsection.features .avatar img{\\n  width: 100%;\\n  height: auto;\\n  \\n}\\n@media (max-width: 768px){\\n  section.features .avatar{\\n      padding-top: 0;\\n    }\\n}\\nsection.features .section-heading {\\n  margin-bottom: 100px; }\\n  section.features .section-heading h2 {\\n    margin-top: 0; }\\n  section.features .section-heading p {\\n    margin-bottom: 0; }\\n\\nsection.features .device-container,\\nsection.features .feature-item {\\n  max-width: 325px;\\n  margin: 0 auto; }\\n\\nsection.features .device-container {\\n  margin-bottom: 100px; }\\n  @media (min-width: 992px) {\\n    section.features .device-container {\\n      margin-bottom: 0; } }\\n\\nsection.features .feature-item {\\n  padding-top: 50px;\\n  padding-bottom: 50px;\\n  text-align: center; }\\n  section.features .feature-item h3 {\\n    font-size: 30px; }\\n  section.features .feature-item i {\\n    font-size: 80px;\\n    display: block;\\n    margin-bottom: 15px;\\n    background: -webkit-linear-gradient(to left, #7b4397, #dc2430);\\n    background: linear-gradient(to left, #7b4397, #dc2430);\\n    -webkit-background-clip: text;\\n    -webkit-text-fill-color: transparent; }\\nsection.services{\\n    background-color: rgba(66, 72, 77, 1);\\n    color: #fff;\\n}\\nsection.services .video iframe{\\n  width: 700px;\\n  height: 380px;\\n}\\n\\nsection.services .section-heading hr{\\n  border-color: rgb(89, 95, 101);\\n  margin: 25px 0 25px 0;\\n  max-width: 80%\\n}\\nsection.clients {\\n  background: url(\\\"../img/p-bg.jpg\\\");\\n}\\nsection.clients .item {\\n  width: 20%;\\n  padding:0;\\n  display: inline-table;\\n  text-align: center\\n}\\nsection.clients .item img{\\n  width: auto;\\n  height: 200px;\\n}\\n@media (max-width: 992px) {\\n    section.clients .img-clients .item{\\n      width: 20%;\\n    }\\n    section.services .video iframe{\\n      width: 700px;\\n      height: 380px;\\n    }\\n  }\\n  @media (max-width: 768px) {\\n    section.clients .img-clients .item{\\n      width: 50%;\\n    }\\n    section.services .video iframe{\\n      width: 300px;\\n      height: 280px;\\n    }\\n\\n  }\\n  @media (max-width: 414px) {\\n    section.clients .img-clients .item{\\n      width: 50%;\\n    }\\n    section.clients .item img{\\n      width: 100%;\\n      height: auto;\\n    }\\n  }\\nsection.cta {\\n  position: relative;\\n  padding: 250px 0;\\n  background-image: url(\\\"../img/bg-cta.jpg\\\");\\n  background-position: center;\\n  -webkit-background-size: cover;\\n  -moz-background-size: cover;\\n  -o-background-size: cover;\\n  background-size: cover; }\\n  section.cta .cta-content {\\n    position: relative;\\n    z-index: 1; }\\n    section.cta .cta-content h2 {\\n      font-size: 50px;\\n      max-width: 450px;\\n      margin-top: 0;\\n      margin-bottom: 25px;\\n      color: white; }\\n    @media (min-width: 768px) {\\n      section.cta .cta-content h2 {\\n        font-size: 80px; } }\\n  section.cta .overlay {\\n    position: absolute;\\n    top: 0;\\n    left: 0;\\n    width: 100%;\\n    height: 100%;\\n    background-color: rgba(0, 0, 0, 0.5); }\\n\\nsection.contact {\\n  text-align: center; }\\n  section.contact h2 {\\n    margin-top: 0;\\n    margin-bottom: 25px; }\\n    section.contact h2 i {\\n      color: #dd4b39; }\\n  section.contact ul.list-social {\\n    margin-bottom: 0; }\\n    section.contact ul.list-social li a {\\n      font-size: 40px;\\n      line-height: 80px;\\n      display: block;\\n      width: 80px;\\n      height: 80px;\\n      color: white;\\n      border-radius: 100%; }\\n    section.contact ul.list-social li.social-twitter a {\\n      background-color: #1da1f2; }\\n      section.contact ul.list-social li.social-twitter a:hover {\\n        background-color: #0d95e8; }\\n    section.contact ul.list-social li.social-facebook a {\\n      background-color: #3b5998; }\\n      section.contact ul.list-social li.social-facebook a:hover {\\n        background-color: #344e86; }\\n    section.contact ul.list-social li.social-google-plus a {\\n      background-color: #dd4b39; }\\n      section.contact ul.list-social li.social-google-plus a:hover {\\n        background-color: #d73925; }\\n\\nfooter {\\n  padding: 25px 0;\\n  text-align: center;\\n  color: rgba(255, 255, 255, 0.3);\\n  background-color: #222222; }\\n  footer p {\\n    font-size: 12px;\\n    margin: 0; }\\n  footer ul {\\n    margin-bottom: 0; }\\n    footer ul li a {\\n      font-size: 12px;\\n      color: rgba(255, 255, 255, 0.3); }\\n      footer ul li a:hover, footer ul li a:focus, footer ul li a:active, footer ul li a.active {\\n        text-decoration: none; }\\n\\n/*.bg-primary {\\n  background: #fdcc52;\\n  background: -webkit-linear-gradient(#fdcc52, #fdc539);\\n  background: linear-gradient(#fdcc52, #fdc539); }*/\\n.bg-primary {\\n  background: #fdcc52;\\n  background: linear-gradient(-45deg,#1C6BC8,#DE3091);\\n  background: -webkit-linear-gradient(-45deg, #1C6BC8, #DE3091); }\\n.bg-pattent {\\n  background: url(\\\"../img/p-bg.jpg\\\");  }\\n\\n.text-primary {\\n  color: #fdcc52; }\\n\\n.no-gutter > [class*=\'col-\'] {\\n  padding-right: 0;\\n  padd